import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import {
  Button,
  Typography,
  Box,
  Paper,
  AppBar,
  Toolbar,
  Container,
  Snackbar,
  Alert,
} from '@mui/material';
import ConnectionForm from './components/ConnectionForm';

function App() {
  const [connected, setConnected] = useState(false);
  const [receiverIp, setReceiverIp] = useState('');
  const [receiverPort, setReceiverPort] = useState(9090);

  const handleConnect = (ip, port) => {
    setReceiverIp(ip);
    setReceiverPort(port);
    setConnected(true);
  };

  return (
    <>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6">QuickLAN File Sender</Typography>
        </Toolbar>
      </AppBar>
      <Container maxWidth="sm" sx={{ mt: 4 }}>
        {!connected ? (
          <ConnectionForm onConnect={handleConnect} />
        ) : (
          <Paper sx={{ p: 3 }}>
            <Typography variant="h6" gutterBottom>
              Connected to {receiverIp}:{receiverPort}
            </Typography>
            {/* Further dashboard components will be added here */}
            <Box sx={{ mt: 2 }}>
              <Typography variant="body2" color="text.secondary">
                Dashboard and file transfer features coming soon.
              </Typography>
            </Box>
          </Paper>
        )}
      </Container>
    </>
  );
}

export default App;
