import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import {
  Button,
  TextField,
  Typography,
  Box,
  List,
  ListItem,
  ListItemText,
  Paper,
  AppBar,
  Toolbar,
  Container,
  Snackbar,
  Alert,
  LinearProgress,
} from '@mui/material';
import SockJS from 'sockjs-client';
import { Client } from '@stomp/stompjs';

function App() {
  const [files, setFiles] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [receiverIp, setReceiverIp] = useState('');
  const [receiverPort, setReceiverPort] = useState(9090);
  const [progressMessages, setProgressMessages] = useState([]);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'info' });
  const [uploading, setUploading] = useState(false);
  const stompClient = useRef(null);

  useEffect(() => {
    fetchFiles();
    connectWebSocket();
    return () => {
      if (stompClient.current) {
        stompClient.current.deactivate();
      }
    };
  }, []);

  const fetchFiles = async () => {
    try {
      const response = await axios.get('http://localhost:9092/api/files');
      setFiles(response.data);
    } catch (error) {
      console.error('Error fetching files:', error);
      showSnackbar('Failed to fetch files', 'error');
    }
  };

  const connectWebSocket = () => {
    const socket = new SockJS('http://localhost:9092/ws/progress');
    stompClient.current = new Client({
      webSocketFactory: () => socket,
      onConnect: () => {
        stompClient.current.subscribe('/topic/progress', (message) => {
          setProgressMessages((prev) => [...prev, message.body]);
        });
      },
      onStompError: (frame) => {
        console.error('Broker reported error: ' + frame.headers['message']);
        console.error('Additional details: ' + frame.body);
        showSnackbar('WebSocket error: ' + frame.headers['message'], 'error');
      },
    });
    stompClient.current.activate();
  };

  const showSnackbar = (message, severity = 'info') => {
    setSnackbar({ open: true, message, severity });
  };

  const handleCloseSnackbar = () => {
    setSnackbar({ ...snackbar, open: false });
  };

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleSendFile = async () => {
    if (!selectedFile || !receiverIp || !receiverPort) {
      showSnackbar('Please select a file and enter receiver IP and port.', 'warning');
      return;
    }

    setUploading(true);
    try {
      const formData = new FormData();
      formData.append('file', selectedFile);

      // Upload file to backend
      await axios.post('http://localhost:9092/api/files/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      // Send file via TCP
      await axios.post('http://localhost:9092/api/send', null, {
        params: {
          receiverIp,
          receiverPort,
          filePath: `received_files/${selectedFile.name}`,
        },
      });

      showSnackbar('File sent successfully!', 'success');
      fetchFiles();
    } catch (error) {
      console.error('Error sending file:', error);
      showSnackbar('Failed to send file.', 'error');
    } finally {
      setUploading(false);
    }
  };

  return (
    <>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6">QuickLAN File Sender</Typography>
        </Toolbar>
      </AppBar>
      <Container maxWidth="sm" sx={{ mt: 4 }}>
        <Paper sx={{ p: 3 }}>
          <Box sx={{ mb: 2 }}>
            <input type="file" onChange={handleFileChange} />
          </Box>

          <TextField
            label="Receiver IP"
            value={receiverIp}
            onChange={(e) => setReceiverIp(e.target.value)}
            fullWidth
            sx={{ mb: 2 }}
          />

          <TextField
            label="Receiver Port"
            type="number"
            value={receiverPort}
            onChange={(e) => setReceiverPort(Number(e.target.value))}
            fullWidth
            sx={{ mb: 2 }}
          />

          <Button
            variant="contained"
            onClick={handleSendFile}
            disabled={!selectedFile || !receiverIp || !receiverPort || uploading}
            fullWidth
          >
            {uploading ? 'Sending...' : 'Send File'}
          </Button>

          {uploading && <LinearProgress sx={{ mt: 2 }} />}

          <Typography variant="h6" sx={{ mt: 4 }}>
            Available Files
          </Typography>
          <List sx={{ maxHeight: 200, overflowY: 'auto' }}>
            {files.map((file) => (
              <ListItem key={file.name} divider>
                <ListItemText primary={file.name} secondary={`${file.size} bytes`} />
              </ListItem>
            ))}
          </List>

          <Typography variant="h6" sx={{ mt: 4 }}>
            Progress Updates
          </Typography>
          <Paper
            variant="outlined"
            sx={{ maxHeight: 200, overflowY: 'auto', backgroundColor: '#f5f5f5', p: 1 }}
          >
            {progressMessages.map((msg, index) => (
              <Typography key={index} variant="body2" sx={{ whiteSpace: 'pre-line' }}>
                {msg}
              </Typography>
            ))}
          </Paper>
        </Paper>
      </Container>

      <Snackbar
        open={snackbar.open}
        autoHideDuration={6000}
        onClose={handleCloseSnackbar}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert onClose={handleCloseSnackbar} severity={snackbar.severity} sx={{ width: '100%' }}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </>
  );
}

export default App;
