import React, { useState, useEffect, useCallback } from 'react';
import {
  Box,
  Typography,
  Paper,
  List,
  ListItem,
  ListItemText,
  Snackbar,
  Alert,
  CircularProgress,
  Chip,
  IconButton,
  Tooltip,
} from '@mui/material';
import {
  Refresh as RefreshIcon,
  Send as SendIcon,
  Error as ErrorIcon,
} from '@mui/icons-material';
import axios from 'axios';
import FileUploader from '../components/FileUploader';
import TransferLogs from '../components/TransferLogs';

const DashboardPage = ({ receiverIp, receiverPort }) => {
  const [files, setFiles] = useState([]);
  const [progressMessages, setProgressMessages] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [loading, setLoading] = useState(false);
  const [snackbar, setSnackbar] = useState({ 
    open: false, 
    message: '', 
    severity: 'info' 
  });

  // Memoize fetchFiles to prevent unnecessary re-renders
  const fetchFiles = useCallback(async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:9092/api/files', {
        timeout: 5000, // 5 second timeout
      });
      setFiles(response.data);
    } catch (error) {
      console.error('Error fetching files:', error);
      const errorMessage = error.response?.data?.message || 
                          error.code === 'ECONNABORTED' ? 'Request timeout' : 
                          'Failed to fetch files';
      showSnackbar(errorMessage, 'error');
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchFiles();
  }, [fetchFiles]);

  const showSnackbar = useCallback((message, severity = 'info') => {
    setSnackbar({ open: true, message, severity });
  }, []);

  const handleCloseSnackbar = useCallback(() => {
    setSnackbar(prev => ({ ...prev, open: false }));
  }, []);

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const validateInputs = () => {
    if (!receiverIp || !receiverPort) {
      showSnackbar('Receiver IP and port must be set.', 'warning');
      return false;
    }
    // Basic IP validation
    const ipRegex = /^(\d{1,3}\.){3}\d{1,3}$/;
    if (!ipRegex.test(receiverIp)) {
      showSnackbar('Invalid IP address format.', 'warning');
      return false;
    }
    // Port validation
    const port = parseInt(receiverPort);
    if (isNaN(port) || port < 1 || port > 65535) {
      showSnackbar('Port must be between 1 and 65535.', 'warning');
      return false;
    }
    return true;
  };

  const handleFileUpload = async (file) => {
    if (!validateInputs()) return;

    // File size validation (e.g., max 100MB)
    const maxSize = 100 * 1024 * 1024; // 100MB
    if (file.size > maxSize) {
      showSnackbar('File size exceeds 100MB limit.', 'warning');
      return;
    }

    setUploading(true);
    const startTime = Date.now();
    
    try {
      const formData = new FormData();
      formData.append('file', file);
      
      // Add progress message
      setProgressMessages(prev => [...prev, 
        `${new Date().toLocaleTimeString()}: Starting upload of ${file.name}`
      ]);

      // Upload file to backend with progress tracking
      await axios.post('http://localhost:9092/api/files/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
        timeout: 60000, // 60 second timeout for uploads
        onUploadProgress: (progressEvent) => {
          const percentCompleted = Math.round(
            (progressEvent.loaded * 100) / progressEvent.total
          );
          setProgressMessages(prev => {
            const newLogs = [...prev];
            const lastIndex = newLogs.length - 1;
            if (lastIndex >= 0 && newLogs[lastIndex].includes('Upload progress:')) {
              newLogs[lastIndex] = `${new Date().toLocaleTimeString()}: Upload progress: ${percentCompleted}%`;
            } else {
              newLogs.push(`${new Date().toLocaleTimeString()}: Upload progress: ${percentCompleted}%`);
            }
            return newLogs;
          });
        }
      });

      setProgressMessages(prev => [...prev, 
        `${new Date().toLocaleTimeString()}: Upload completed. Initiating transfer...`
      ]);

      // Send file via TCP
      await axios.post('http://localhost:9092/api/send', null, {
        params: {
          receiverIp,
          receiverPort,
          filePath: `received_files/${file.name}`,
        },
        timeout: 30000, // 30 second timeout for sending
      });

      const duration = ((Date.now() - startTime) / 1000).toFixed(2);
      const successMessage = `File "${file.name}" sent successfully in ${duration}s!`;
      
      setProgressMessages(prev => [...prev, 
        `${new Date().toLocaleTimeString()}: ${successMessage}`
      ]);
      
      showSnackbar(successMessage, 'success');
      await fetchFiles(); // Refresh file list
      
    } catch (error) {
      console.error('Error sending file:', error);
      const errorMessage = error.response?.data?.message || 
                          error.code === 'ECONNABORTED' ? 'Operation timeout' :
                          error.code === 'ECONNREFUSED' ? 'Connection refused' :
                          'Failed to send file';
      
      setProgressMessages(prev => [...prev, 
        `${new Date().toLocaleTimeString()}: Error - ${errorMessage}`
      ]);
      
      showSnackbar(errorMessage, 'error');
    } finally {
      setUploading(false);
    }
  };

  const handleSendExistingFile = async (fileName) => {
    if (!validateInputs()) return;

    try {
      await axios.post('http://localhost:9092/api/send', null, {
        params: {
          receiverIp,
          receiverPort,
          filePath: `received_files/${fileName}`,
        },
        timeout: 30000,
      });
      
      showSnackbar(`File "${fileName}" sent successfully!`, 'success');
      setProgressMessages(prev => [...prev, 
        `${new Date().toLocaleTimeString()}: Sent existing file: ${fileName}`
      ]);
    } catch (error) {
      console.error('Error sending existing file:', error);
      showSnackbar('Failed to send file.', 'error');
    }
  };

  const getConnectionStatus = () => {
    return receiverIp && receiverPort ? 'connected' : 'disconnected';
  };

  return (
    <Box sx={{ p: 2 }}>
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
        <Typography variant="h5" sx={{ flexGrow: 1 }}>
          File Transfer Dashboard
        </Typography>
        <Chip 
          label={`${getConnectionStatus()}`}
          color={getConnectionStatus() === 'connected' ? 'success' : 'error'}
          size="small"
          sx={{ mr: 1 }}
        />
        <Tooltip title="Refresh file list">
          <IconButton onClick={fetchFiles} disabled={loading}>
            {loading ? <CircularProgress size={20} /> : <RefreshIcon />}
          </IconButton>
        </Tooltip>
      </Box>

      {receiverIp && receiverPort && (
        <Paper sx={{ p: 2, mb: 3, bgcolor: 'background.default' }}>
          <Typography variant="body2" color="text.secondary">
            Target: {receiverIp}:{receiverPort}
          </Typography>
        </Paper>
      )}

      <FileUploader onFileUpload={handleFileUpload} uploading={uploading} />

      <Box sx={{ display: 'flex', alignItems: 'center', mt: 4, mb: 2 }}>
        <Typography variant="h6" sx={{ flexGrow: 1 }}>
          Available Files ({files.length})
        </Typography>
      </Box>

      <Paper sx={{ maxHeight: 300, overflowY: 'auto', mb: 3 }}>
        {files.length === 0 ? (
          <Box sx={{ p: 3, textAlign: 'center' }}>
            <Typography color="text.secondary">
              No files available. Upload a file to get started.
            </Typography>
          </Box>
        ) : (
          <List>
            {files.map((file, index) => (
              <ListItem 
                key={`${file.name}-${index}`} 
                divider={index < files.length - 1}
                secondaryAction={
                  <Tooltip title="Send this file">
                    <IconButton 
                      edge="end" 
                      onClick={() => handleSendExistingFile(file.name)}
                      disabled={!receiverIp || !receiverPort}
                    >
                      <SendIcon />
                    </IconButton>
                  </Tooltip>
                }
              >
                <ListItemText 
                  primary={file.name} 
                  secondary={
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                      <Typography variant="body2" color="text.secondary">
                        {formatFileSize(file.size)}
                      </Typography>
                      {file.lastModified && (
                        <Typography variant="body2" color="text.secondary">
                          â€¢ Modified: {new Date(file.lastModified).toLocaleDateString()}
                        </Typography>
                      )}
                    </Box>
                  }
                />
              </ListItem>
            ))}
          </List>
        )}
      </Paper>

      <TransferLogs logs={progressMessages} />

      <Snackbar
        open={snackbar.open}
        autoHideDuration={6000}
        onClose={handleCloseSnackbar}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert 
          onClose={handleCloseSnackbar} 
          severity={snackbar.severity} 
          sx={{ width: '100%' }}
          variant="filled"
        >
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default DashboardPage;
